import { useState } from 'react';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';
import UserComment from './UserComment';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };


const UserProfilePage = ({
  username,
  profilePicture,
  userPosts,
  followersCount,
  followingCount,
  posts,
}) => {
  const [selectedPost, setSelectedPost] = useState(null);

  const handleClickPost = (postId) => {
    setSelectedPost(postId);
  };

  useEscapeKeyHandler(() => setSelectedPost(false));

  return (
    <div className="user-profile-info">
      <div className="profile-header">
        <div className="user-photo-header">
          <img src={profilePicture} alt="Profile" />
          <h1>{username}</h1>
          <div className="btn-container-user-profile">
            <button className="edit-profile-user-btn">Edit Profile</button>
            <button className="view-archive-profile-user">View archive</button>
          </div>
          <div className="follow-info">
            <span>{userPosts} posts</span>
            <span>{followersCount} followers</span>
            <span>{followingCount} following</span>
          </div>
        </div>
      </div>
      <h2>
        <i className="fa-solid fa-table-cells"></i>Posts
      </h2>
      <div className="user-posts">
        {posts &&
          posts.map((post) => (
            <Slider key={post.id} className="post-slider" {...sliderSettings}>
              {post.images.map((image, index) => (
                <div key={index} className="post" onC>
                  <img src={image.url} alt="Post" />
                  {selectedPost === post.id && (
                    <UserComment
                      key={post.id}
                      imageUrl={image.url}
                      profilePicture={profilePicture}
                      username={username}
                    />
                  )}
                </div>
              ))}
            </Slider>
          ))}
      </div>
      {selectedPost && (
        <div className="page-overlay" onClick={() => setSelectedPost(null)}>
          {/* Additional content for the page overlay */}
        </div>
      )}
    </div>
  );
};

export default UserProfilePage;
