import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { updateUserProfile } from '../../api/auth';
import { updateUserDataAction } from '../../context/auth/actions';

const UserInfo = () => {
  const { state, dispatch } = useAuthContext();
  const { user } = state;
  console.log(state);
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    username: user.username || '',
    name: user.name || '',
    lastName: user.lastName || '',
    age: user.age || '',
    email: user.email || '',
    newPassword: '',
    confirmPassword: '',
  });

  const passwordInputRef = useRef(null); // Define password input ref


  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  }, []);

  useEffect(() => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      username: user.username || '',
      name: user.name || '',
      lastName: user.lastName || '',
      age: user.age || '',
      email: user.email || '',
    }));
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateUserProfile(user.userId, formData);
      const updatedUser = { ...user, ...formData };
      dispatch(updateUserDataAction(updatedUser));
      setMessage('Successfully updated profile information');
      console.log(updateUserDataAction(updatedUser, state));
    } catch (error) {
      console.error('Failed to update user profile:', error);
    }
  };

  const togglePasswordVisibility = (e) => {
    e.preventDefault();
    const passwordInput = passwordInputRef.current;
    if (passwordInput.type === 'password') {
      passwordInput.type = 'text';
    } else {
      passwordInput.type = 'password';
    }
  };

  return (
    <div className="user-info-modal-wrapper">
      <form onSubmit={handleSubmit}>
        {Object.entries(formData).map(([key, value]) => (
          <div className="form-inputs-update-profile" key={key}>
            <label htmlFor={key}>
              {key.charAt(0).toUpperCase() + key.slice(1)}:
            </label>
            <input
              type={key.includes('Password') ? 'password' : 'text'}
              id={key}
              name={key}
              value={value}
              onChange={handleChange}
              disabled={key === 'username'}
              autoComplete={key === 'password' ? 'on' : 'off'}
              ref={key === 'newPassword' ? passwordInputRef : null} // Assign ref to password input
              />
              {key === 'newPassword' && (
                <button className="password-toggle" onClick={togglePasswordVisibility}>
                  {passwordInputRef.current && passwordInputRef.current.type === 'password' ? (
                    <i className="fa-solid fa-eye"></i>
                  ) : (
                    <i className="fa-solid fa-eye-slash"></i>
                  )}
                </button>
              )}
          </div>
        ))}
        <button type="submit">Save Changes</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UserInfo;
