// PostDetail.js
import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import UserComment from './UserComment';
// import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { userLikes } from '../../api/services/userServices';

const PostDetail = ({ post }) => {
  const { state } = useAuthContext();
  const { user } = state;
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');


  return (
    <div className="post-detail p-4 bg-black rounded-lg shadow-md">
      <div className="post-detail-image-box">
        <img
          src={post.image}
          alt="Post"
          className="post-detail-image w-full h-auto rounded-lg"
        />
      </div>
      <div className="post-detail-info mt-4">
        <div className="post-detail-userinfo flex items-end">
          <div className="post-detail-image-box w-10 h-10 rounded-full overflow-hidden">
            <img src={user.userAvatar} alt="" />
          </div>
          <p className="ml-4">{user.username}</p>
        </div>
        <UserComment postId={post._id}/>
        <p className="mt-2">{post.description}</p>
        <span
          onClick={() => handleUserLike(post._id)}
          className="cursor-pointer"
        >
                    <i className={`fa-heart ${isLiked ? 'fa-solid text-red-500' : 'fa-regular'}`}></i>
        </span>
        <p className="mt-10">Likes: {likes.length}</p>
        <div>
          <p className="font-semibold">Liked by:</p>
          <ul className="list-disc list-inside">
            {likes.map((like, index) => (
              <li key={index} className="text-red-500">
                {like.username}
              </li>
            ))}
          </ul>
        </div>

      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="text-red-500">{error}</p>}
    </div>
  );
};

export default PostDetail;
