import React, { useState } from 'react';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';
import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { resetPasswordRequest } from '../../api/services/userServices';
import { BarLoader } from 'react-spinners';

const ForgetPasswordModal = ({ onClose }) => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    handleAsyncOperation(
      async () => {
        await resetPasswordRequest();
        onClose();
      },
      setLoading,
      (error) => setMessage(error.message),
    );
  };

  useEscapeKeyHandler(onClose);

  return (
    <div className="password-reset-modal">
      <span className="close" onClick={onClose}>
        &times;
      </span>
      <form onSubmit={handleSubmit}>
        <div className="password-reset-request-input">
          <label htmlFor="email">Recovery Email</label>
          <i className="fa-solid fa-envelope user-email"></i>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            autoComplete="on"
          />
          <button type="submit" disabled={loading}>
            Send
          </button>
        </div>
      </form>
      {error && <p>{error}</p>}
      {loading && (
      <div className="bar-loader" style={{}}>
        <BarLoader color="#fe3c72" />
        {/* <p>Sending password instructions...</p> */}
      </div>

      )}
      {message && <p>{message}</p>}
    </div>
  );
};

export default ForgetPasswordModal;
