import React from 'react'
import { useAuthContext } from '../../context/auth/AuthContextProvider'


const EditProfile = () =>{
    const {state} = useAuthContext();
    const {user} = state;
  return (
    <div className='edit-profile-container'>
        <h3>Edit Profile</h3>
        <div className='user-profile-photo'>
            <div className='photo-name-box'>
                <img src="https://images.unsplash.com/flagged/photo-1570612861542-284f4c12e75f?q=80&w=1000&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8M3x8cGVyc29ufGVufDB8fDB8fHww" alt="" />
            </div>
            <span className='username-span-settings'>{user.userName}</span>
        </div>
    </div>
  )
}

export default EditProfile
