import React, {useState, useEffect} from 'react'
import { personInfo } from '../../api/users'
import { RingLoader } from 'react-spinners'

function Feed() {
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [personInfoData, setPersonInfoData] = useState([])

  useEffect(() => {
    const fetchData = async () => {
      try {
        const person = await personInfo();
         setPersonInfoData(person);
      } catch (error) {
        setError("Error fetching person data" + error.msg)
      } finally {
        setLoading(false)
      }
    };

    fetchData();
  }, [])


  return (
    <div className='user-feed-container'>
      {error && <h1>{error}</h1>}
      {loading && (
          <div className="bar-loader" style={{}}>
            <RingLoader color="#fe3c72" />
          </div>
        )}
        <div className="person-info-api">
          {personInfoData.map((person) => (
            <div key={person.id} className='person-info-box'>
              <div className='person-photo-container'>
                <img src={person.image} alt="Person Image" />
              </div>
            <h2>Name: {person.name}</h2>
            <p>Email: {person.email}</p>
            <p>Age: {person.age}</p>
            <p>State: {person.address.state}</p>
            <p>Address: {person.address.city}</p>
            <p>Street: {person.address.street}</p>
              <span>Interests: {person.interests.join(', ')}</span>
              </div>
              ))}
            </div>
      </div>
  )
}

export default Feed
