import React, { useState, useEffect, useRef } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';

const Chat = ({ chatMessages }) => {
  const chatContainerRef = useRef(null);
  const [inputMessage, setInputMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { state } = useAuthContext();
  const { user: currentUser } = state;

  const handleMessageChange = (event) => {
    setInputMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (inputMessage.trim() === '') {
      return; // Prevent sending empty messages
    }
    // Add the message to the list of messages
    setMessages([...messages, { text: inputMessage, sender: currentUser.userName }]);
    // Clear the input field
    setInputMessage('');
  };

  const handleFormSubmit = (event) => {

    event.preventDefault();
    handleSubmit();
  };

  useEffect(() => {
    // Scroll chat container to the bottom whenever messages change
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop =
        chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div className="chat-container">
      <div ref={chatContainerRef} className="chat-messages">
        {messages.map((msg, index) => (
           <div key={index} className={`message ${msg.sender === currentUser.userName ? 'current-user' : 'other-user'}`}>
            <span className="sender">{currentUser.userName}</span>:{' '}
            <span className="message-text-chat">{msg.text}</span>
          </div>
        ))}
      </div>
      <form onSubmit={handleFormSubmit} className="chat-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={inputMessage}
          onChange={handleMessageChange}
        />
        <button type="submit" className="send-message-button">
          Send
        </button>
      </form>
    </div>
  );
};

export default Chat;
