export const fetchData = async () => {
  try {
    const urlUsers = "http://localhost:5500/api/users";
    const urlPosts = "http://localhost:5500/api/posts";

    // Fetch user data
    const responseUsers = await fetch(urlUsers);
    if (!responseUsers.ok) {
      throw new Error('Failed to fetch user data');
    }
    const userData = await responseUsers.json();

    // Fetch post data
    const responsePosts = await fetch(urlPosts);
    if (!responsePosts.ok) {
      throw new Error('Failed to fetch post data');
    }
    const postData = await responsePosts.json();

    // Return an object containing both user and post data
    return { users: userData, posts: postData };
  } catch (error) {
    console.error('Error fetching data:', error.message);
    throw error; // Re-throw the error to propagate it to the caller
  }
};
