import React, { useState, useEffect } from 'react';

const UserComment = ({ imageUrl, username, profilePicture }) => {
  const [usersInfo, setUsersInfo] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setUsersInfo(usersInfo);
      } catch (error) {
        setError('Error fetching users data' + error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  return (
    <>
      {usersInfo && (
        <div className="user-post-full-width-container">
          <div className="user-post-photo-fullwidth">
            <img src={usersInfo.imageUrl} alt="" />
          </div>
          <section className="user-comment-section">
            <div className="comment-section-header">
              <img src={usersInfo.profilePicture} alt="" />
              <span>{userInfo.username}</span>
            </div>
            <div className="comment-list">
              {/* Display existing comments here */}
              {/* Example: */}
              {/* <div className="comment-item">
            <img src={profilePicture} alt="" />
            <span>{username}: Comment text</span>
          </div> */}
            </div>
            <div className="comment-input">
              {/* <img src={profilePicutre} alt="" /> */}
              <input type="text" placeholder="Add a comment..." />
              <button>Post</button>
            </div>
          </section>
        </div>
      )}
    </>
  );
};

export default UserComment;
