import React, { useState } from 'react';
import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { sendVerificationSms, verifySms } from '../../api/services/userServices';
import { BarLoader } from 'react-spinners';

function SmsForm() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [to, setTo] = useState('');
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true)
    await handleAsyncOperation(
      async () => {
        const data = await sendVerificationSms(to, message);
        setResponse(data);
      },
      setLoading,
      (error) => setError(error.message),
    );
  };

  const handleVerifyCode = async (e) => {
    e.preventDefault();
    setLoading(true);
    await handleAsyncOperation(async () =>{
        const data = await verifySms()
    })
  }

  return (
    <div>
      <h1>Send SMS</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Phone Number:</label>
          <input
            type="text"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Message:</label>
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
          />
        </div>
        <button type="submit">Send SMS</button>
      </form>

      {response && <div>Success: {JSON.stringify(response)}</div>}
      {error && <div>Error: {error}</div>}
      {loading && (
        <div className="bar-loader" style={{}}>
          <BarLoader color="#fe3c72" />
        </div>
      )}
    </div>
  );
}

export default SmsForm;
