import React, { useState } from 'react';
import { RingLoader } from 'react-spinners';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { postUserComment } from '../../api/services/userServices';

const UserComment = (postId ) => {
  const { state } = useAuthContext();
  const { user } = state;
  const [commentText, setCommentText] = useState('');
  const [userComments, setUserComments] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const handleUserComment = async () => {
    setLoading(true);
    setError('');
    try {
      console.log("Post id: ", postId)
      const newComment = await postUserComment(postId._id, user.userId, commentText);
      setUserComments([...userComments, newComment]); // Append the new comment to the existing comments array
      console.log('Comment: ', newComment);
      setCommentText('')
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="comment-component">
      <div className="comment-list">
        <div className="comment-list">
          <div className="comment-item">
            {/* <img src={profilePicture} alt="" /> */}
            <span>{user.username}: Comment text</span>
          </div>
        </div>
      </div>
      <div className="comment-input">
        {/* <img src={profilePicutre} alt="" /> */}
        <input type="text" placeholder="Add a comment..." />
        <button onClick={() => handleUserComment(postId, userId, text)}>
          Post
        </button>
      </div>
    </div>
  );
};

export default UserComment;
