import React, { useState, useEffect } from 'react';
import { fetchUserFollowers, getUserPosts } from '../../api/services/userServices';
import { BarLoader } from 'react-spinners';
import DeletePost from './DeletePost';
import PostDetail from './PostDetail';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';

const UserPosts = ({ userId, username, posts, setPosts }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedPost, setSelectedPost] = useState(null); // Track the selected post for modal display
  const [followers, setfollowers] = useState(second)

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const data = await getUserPosts(username);
        const userFollowers = await fetchUserFollowers(userId)
        setPosts(data);
        console.log('Data', data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };
    fetchPosts();
  }, [username, setPosts, userId]);

  const handleDelete = (postId) => {
    setPosts(posts.filter((post) => post._id !== postId));
  };

  const handlePostClick = (post) => {
    setSelectedPost(post);
  };

  const closeModal = () => {
    setSelectedPost(null);
  };

  useEscapeKeyHandler(closeModal);

  if (loading)
    return (
      <div className="bar-loader" style={{}}>
        <BarLoader color="#fe3c72" />
      </div>
    );
  if (error) return <p>{error}</p>;

  return (
    <div className="container">
      <div className="gallery">
        {posts.map((post) => (
          <div className="gallery-item" key={post._id}>
            <img
              src={post.image}
              alt="Post"
              onClick={() => handlePostClick(post)}
              className="gallery-image"
            />
            <div className="gallery-item-info">
              <DeletePost postId={post._id} onDelete={handleDelete} />
            </div>
          </div>
        ))}
      </div>
      {selectedPost && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <PostDetail post={selectedPost} />
          </div>
        </div>
      )}
    </div>
  );
};

export default UserPosts;
