import React, { useRef } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import styles from '../UserProfile/user.module.scss';

const UserProfile = () => {
  const { state, dispatch } = useAuthContext();
  const { user } = state;
  const fileInputRef = useRef(null); // Reference to the file input element

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      const imageData = reader.result;
      // Save the image data to localStorage
      localStorage.setItem('profilePicture', imageData);
      // Update the user's profile picture in the state
      dispatch({ type: 'UPDATE_PROFILE_PICTURE', payload: { profilePicture: imageData } });
    };

    reader.readAsDataURL(file); // Convert file to data URL
  };

  const handleClick = () => {
    fileInputRef.current.click(); // Trigger file input click when the user clicks on the profile picture
  };

  return (
    <div className={styles.userProfileContainer}>
      <p>{user.userName}</p>
      <div className={styles.profilePicture} onClick={handleClick}>
        {/* Display the current profile picture */}
        <img src={user.profilePicture || localStorage.getItem('profilePicture')} alt="Profile" />
        {/* Hidden file input element */}
        <input
          type="file"
          ref={fileInputRef} // Attach the ref to the file input element
          style={{ display: 'none' }} // Hide the file input
          onChange={handleFileSelect} // Attach onChange event handler
        />
      </div>
    </div>
  );
};

export default UserProfile;
