const signUp = async (user) => {
  const url = 'http://localhost:5500/api/users/register';
  const resp = await fetch(url, {
    method: 'POST',
    body: JSON.stringify(user),
    headers: {
      'Content-Type': 'application/json',
    },
  });
  const data = await resp.json();
  if (resp.ok) {
    return data;
  }
  throw new Error(data.message);
};

const signIn = async (user) => {
  const url = 'http://localhost:5500/api/users/login';
  const resp = await fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(user),
  });
  const data = await resp.json();
  if (resp.ok) {
    return data;
  }
  throw new Error(data.message);
};

const updateUserProfile = async (userId, updateData) => {
  const url = `http://localhost:5500/api/users/update-profile/${userId}`;
  const token = localStorage.getItem('accessToken');


  const resp = await fetch(url, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify(updateData),
  });

  if (resp.ok) {
    const responseData = await resp.json();

    // Check if there's a new access token in the response headers
    const newAccessToken = resp.headers.get('Authorization');
    if (newAccessToken) {
      // Update the access token in local storage
      localStorage.setItem('accessToken', newAccessToken);
    }

    return responseData; // Return the response data
  } else {
    throw new Error('Failed to update user profile');
  }
  }

export { signUp, signIn, updateUserProfile };
