import { useNavigate } from 'react-router-dom';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { logInAction } from '../../context/auth/actions';
import { HOME_PAGE } from '../../constants/routes';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { signIn } from '../../api/auth';
import { BarLoader } from 'react-spinners';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { handleAsyncOperation } from '../../utils/handleAsyncOperation';

const Form = () => {
  const { dispatch } = useAuthContext();
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);

  const formik = useFormik({
    initialState: {
      username: '',
      password: '',
    },
    validationSchema: Yup.object({
      username: Yup.string().required("User name is required"),
      password: Yup.string().required("Password is required"),
    }),
    onSubmit: async (values) => {
      await handleAsyncOperation(async () => {
      const data = signIn(values)
        dispatch(logInAction(data));
        navigate(HOME_PAGE);
      })
    }
  })

  const togglePasswordVisibility = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };



  return (
    <form className="sign-in" onSubmit={formik.handleSubmit}>
      <label htmlFor="username">User Name</label>
      <div className="input-container">
        <input
          className="input-field"
          autoComplete="true"
          value={info.username}
          type="text"
          name="username"
          value={formik.values.username}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        <i className="fa-solid fa-user user-icon"></i>
      </div>

      <label htmlFor="password">Password</label>
      <div className="input-container">
        <input
          className="input-field"
          autoComplete="true"
          type={showPassword ? "text" : "password"}
          value={info.password}
          name="password"
          onChange={(e) => {
            setInfo((prev) => {
              return { ...prev, [e.target.name]: e.target.value };
            });
          }}
        />
        <i className="fa-solid fa-lock password-icon"></i>
        {showPassword ? (
          <i className="fa-solid fa-eye" onClick={togglePasswordVisibility}></i>
        ) : (
          <i
            className="fa-solid fa-eye-slash"
            onClick={togglePasswordVisibility}
          ></i>

        )}
      </div>
      {loading && (
        <div className="bar-loader" style={{}}>
          <BarLoader color="#fe3c72" />
        </div>
      )}
      {message && <h4>{message}</h4>}
      {/* {message && <h5>{message}</h5>} */}
      <button className="login-button" onClick={submitHandler}>
        Login
      </button>
      <Link className="back-home" to={HOME_PAGE}>
        <button className="back-home-button">Back to home</button>
      </Link>
    </form>
  );
};

export default Form;
