import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import CreatePost from './CreatePost';
import { Link } from 'react-router-dom';
import UserPosts from './UserPosts';

const UserProfilePage = () => {
  const { state } = useAuthContext();
  const { user } = state;
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreatePost, setShowCreatePost] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const userPosts = await getUserPosts(user.username);
        setPosts(userPosts);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(false);
      }
    };

    fetchPosts();
  }, [user.username]);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <img src={user.userAvatar} alt="Profile" className="profile-picture" />
        <div className="profile-info">
          <h1>{user.username}</h1>
          <div className="profile-stats">
            <span>{posts.length} posts</span>
            <span>{user.followers.length} followers</span>
            <span>{user.following.length} following</span>
          </div>
          <div className="profile-actions">
            <Link to={`/accounts/${user.username}/edit`}>
              <button>Edit Profile</button>
            </Link>
            <button>View Archive</button>
          </div>
        </div>
      </div>
      <div className="create-post-button-container" onClick={() => setShowCreatePost(!showCreatePost)}>
        <i className="fa-regular fa-square-plus create-post-icon"></i>
        Create Post
      </div>
      {showCreatePost && (
        <div className="page-overlay">
          <CreatePost user={user} setPosts={setPosts} onClose={() => setShowCreatePost(false)} />
        </div>
      )}
      <div className="profile-posts">
        <UserPosts posts={posts} setPosts={setPosts} />
      </div>
    </div>
  );
};

export default UserProfilePage;
