import React, { useState, useEffect } from 'react';
import { MenuItem } from '../Home/SettingsMenu';
import UserComment from '../../components/UserProfile/UserComment';

const Archive = ({ post }) => {
  const [savedPosts, setSavedPosts] = useState([]);

  const handleSavePost = (post) => {
    setSavedPosts((prevPosts) => [...prevPosts, post]);
  };

  useEffect(() => {
    if (post) {
      setSavedPosts((prevPosts) => [...prevPosts, post]);
    }
  }, [post]);

  return (
    <div className="archive-container">
      <MenuItem icon="fa-regular fa-bookmark save-icon" text="Saved" />
      <div className="line"></div>
      <div className="saved-posts-container">
        {savedPosts.map((post, index) => (
          <UserComment
            key={index}
            imageUrl={post.imageUrl}
            profilePicture={post.profilePicture}
            username={post.username}
            onSave={() => handleSavePost(post)}
          />
        ))}
      </div>
    </div>
  );
};

export default Archive;
