import { jwtDecode } from 'jwt-decode';
import { date } from 'yup';

function parseJWT(token) {
  const data = jwtDecode(token);
  return data;
}

function parseJWTgoogle(googleToken) {
  const data = jwtDecode(googleToken);
  return data;
}

function isTOkenValid(token) {
  const currentTime = Date.now() / 1000;
  const decoded = jwtDecode(token);
  return decoded.exp > currentTime;
}

function isGoogleTokenValid(googleToken) {
  const currentTime = Date.now() / 1000;
  const decoded = jwtDecode(googleToken);
  return decoded.exp > currentTime;
}

function toggleLocalStorage(token, refreshToken, googleToken) {
  if (token && refreshToken && googleToken) {
    localStorage.setItem('accessToken', token);
    localStorage.setItem('refreshToken', refreshToken);
    localStorage.setItem('googleToken', googleToken);
  } else {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    localStorage.removeItem('user');
    localStorage.removeItem('googleToken');
  }
}

export { toggleLocalStorage, isTOkenValid, parseJWT };
