import React from 'react';
import { GoogleLogin } from 'react-google-login';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { logInAction } from '../../context/auth/actions';
import { HOME_PAGE } from '../../constants/routes';
import { useNavigate } from 'react-router-dom';

const GoogleSignInButton = () => {
  const { dispatch } = useAuthContext();
  const navigate = useNavigate();

  const responseGoogle = (response) => {
    // Ensure the response contains the necessary data
    if (response.profileObj && response.tokenId) {
      console.log(response);
      // Dispatch action to log in the user with received data
      dispatch(logInAction(response.profileObj));
      // Redirect to the home page
      navigate(HOME_PAGE);
    } else {
      console.error('Google login failed: No profile or token found');
    }
  };

  const onFailure = (error) => {
    // Handle failure of Google OAuth authentication
    console.error('Google login failed', error);
  };

  return (
    <GoogleLogin
      clientId="230625856455-h70i0k40gr1pf3d0jhfsms7ul8gjkk15.apps.googleusercontent.com"
      buttonText="Sign in with Google"
      onSuccess={responseGoogle}
      onFailure={onFailure}
      cookiePolicy={'single_host_origin'}
    />
  );
};

export default GoogleSignInButton;
