import React, { useState, useEffect } from 'react';
import { MenuItem } from '../Home/SettingsMenu';
import UserComment from '../../components/UserProfile/UserComment';

const Archive = ({ posts, movePostToArchive }) => {
  const [savedPosts, setSavedPosts] = useState([]);

  useEffect(() => {
    if (posts) {
      // Append the new post to the existing savedPosts array
      setSavedPosts(prevPosts => [...prevPosts, post]);
    }
  }, [post]); // Trigger effect whenever the post prop changes

  return (
    <div className="archive-container">
      <MenuItem icon="fa-regular fa-bookmark save-icon" text="Saved" />
      <div className="line"></div>
      <div className="saved-posts-container">
        {savedPosts.map((savedPost, index) => (
          <UserComment
            key={index}
            imageUrl={savedPost.imageUrl}
            profilePicture={savedPost.profilePicture}
            username={savedPost.username}
          />
        ))}
      </div>
    </div>
  );
};

export default Archive;
