import React from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';

// ProfilePhoto component
const ProfilePhoto = ({ imageUrl, userName }) => (

  <div className="user-profile-photo">
    <div className="photo-name-box">
      <img src={imageUrl} alt="" />
      <span className="username-span-settings">@{userName}</span>
    </div>
    <div>
      <button className="button-change-photo">Change Photo</button>
    </div>
  </div>
);

// Bio component
const Bio = () => (
  <div className="user-bio-container">
    <div>
      <h4>Bio</h4>
    </div>
    <textarea
      name="bio"
      id="user-bio"
      cols="73"
      rows="2"
      placeholder="Bio"
    ></textarea>
  </div>
);

// Gender component
const Gender = () => (
  <div className="user-gender-container">
    <div>
      <h4>Gender</h4>
    </div>
    <select name="gender" id="select-gender">
      <option value="male">Male</option>
      <option value="female">Female</option>
      <option value="prefer-not-tosay">Prefer not to say</option>
    </select>
  </div>
);

// Main EditProfile component
const EditProfile = ({ userName, imageUrl }) => (

  <div className="edit-profile-container">
    <div>
      <h3>Edit Profile</h3>
    </div>
    <ProfilePhoto imageUrl={imageUrl} userName={userName} />
    <Bio />
    <Gender />
    <button className="submit-button-userprofile">Submit</button>
  </div>
);

export default EditProfile;
