import React, { useEffect, useState } from 'react';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';
import UserComment from './UserComment';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Link } from 'react-router-dom';
import { ARCHIVE } from '../../constants/routes';
import { usersData, usersPosts } from '../../api/users';
import { RingLoader } from 'react-spinners';

const UserProfilePage = () => {
  const [selectedPost, setSelectedPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [userData, setUserData] = useState([]);
  const [userPostsData, setUserPostsData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const users = await usersData();
        setUserData(users);
        setLoading(false);
      } catch (error) {
        setError('Error fetching user data: ' + error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const posts = await usersPosts();
        setUserPostsData(posts);
        setLoading(false);
      } catch (error) {
        setError('Error fetching user posts: ' + error.message);
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  const handleClickPost = (postId) => {
    setSelectedPost(postId === selectedPost ? null : postId);
  };

  useEscapeKeyHandler(() => setSelectedPost(false));

  return (
    <div className="user-profile-info">
      <div className="profile-header">
        {/* Profile header content */}
      </div>
      <h2>
        <i className="fa-solid fa-table-cells"></i>Posts
      </h2>
      <div className="user-posts">
        {userPostsData.map((post) => (
          <div
            key={post.id}
            className="post"
            onClick={() => handleClickPost(post.id)}
          >
            <Slider>
              {Array.isArray(post.images) &&
                post.images.map((image, index) => (
                  <div key={index}>
                    <img src={image.url} alt={`Post ${index + 1}`} />
                  </div>
                ))}
            </Slider>
            {selectedPost === post.id && (
              <UserComment postId={post.id} />
            )}
          </div>
        ))}
      </div>
      <div>
        {error && <h2>{error}</h2>}
        {loading && (
          <div className="bar-loader">
            <RingLoader color="#fe3c72" />
          </div>
        )}
        <div className="users-info-api">
          {userData.map((user) => (
            <div key={user.id}>
              <h2>Name: {user.name}</h2>
              <p>Email: {user.email}</p>
              {/* Add additional user information here */}
            </div>
          ))}
        </div>
      </div>
      {selectedPost && (
        <div className="page-overlay" onClick={() => setSelectedPost(null)}>
          {/* Additional content for the page overlay */}
        </div>
      )}
    </div>
  );
};

export default UserProfilePage;
