import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { verifyEmailStatus } from '../../api/auth';
import { RingLoader } from 'react-spinners';
import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { jwtDecode } from 'jwt-decode';

const VerifyEmail = () => {
  const { token } = useParams();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [verificationStatus, setVerificationStatus] = useState('Verifying...');
  const navigate = useNavigate();

  const verifyEmail = jwtDecode(token)

  useEffect(() => {
    const verifyEmail = async () => {
      await handleAsyncOperation(
         async () => {
           verifyEmailStatus(token);
           setVerificationStatus('Email Verified Successfully');
           setLoading(false);
           setTimeout(() => {
             navigate('/');
           }, 2000);
         },
         setLoading,
         (error) => setError(error.message),
       );
        }
       verifyEmail();
     }, [token, navigate]);



     console.log("verify email token: ", verifyEmail)


  return (
    <div className="verify-email-modal">
      {error && <h1>{error}</h1>}
      {loading && (
        <div className="bar-loader" style={{}}>
          <RingLoader color="#fe3c72" />
        </div>
      )}
      <h3>{verificationStatus}</h3>
    </div>
  );
};

export default VerifyEmail;
