import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { createNewPost, fetchUserFollowers } from '../../api/services/userServices';
import LogoutButton from '../LogoutButton/LogoutButton';
import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { Link } from 'react-router-dom';
import UserPosts from './UserPosts';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';

const UserProfilePage = () => {
  const { state, dispatch } = useAuthContext();
  const { user } = state;
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [caption, setCaption] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [message, setMessage] = useState('');
  const [posts, setPosts] = useState([]);
  const [showCreatePost, setShowCreatePost] = useState(false)
  const [followers, setFollowers] = useState(second)

  useEffect(() => {
    if (showCreatePost) {
      console.log("ShowCreatedPost", showCreatePost )
      document.body.classList.add('no-scroll');
    } else {
      document.body.classList.remove('no-scroll');
    }
    return () => document.body.classList.remove('no-scroll'); // Clean up on unmount
  }, [showCreatePost]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
  };

  const handleCreatePost = async (e) => {
    e.preventDefault();
    await handleAsyncOperation(
      async () => {
        setLoading(true);
        const formData = new FormData();
        formData.append('caption', caption);
        formData.append('image', imageFile);
        formData.append('userId', user.userId);
        setMessage('Post created');
        console.log('Form data: ', formData);

        const createdPost = await createNewPost(formData);
        // setPosts((prevPosts) => [createdPost, ...prevPosts]);
        setCaption('');
        setImageFile(null);
        setError('');
        setLoading(false);
        setShowCreatePost(false)
      },
      setLoading,
      (error) => setMessage(error.message),
    );
  };

  const handleFollowUser = async (userId) => {
    try {
      setLoading(true);
      const followers = await fetchUserFollowers(userId);
      setFollowers(followers);  // assuming setFollowers is a state setter for storing followers
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setMessage(error.message);
    }
  };

  useEscapeKeyHandler(() => setShowCreatePost(false))

  return (
    <div className="profile-page">
      <div className="profile-header">
        <img src={user.userAvatar} alt="Profile" className="profile-picture" />
        <div className="profile-info">
          <h1>{user.username}</h1>
          <div className="profile-stats">
            <span>{user.posts} posts</span> 
             <span>{user.followers} followers</span>
            <span>{user.following} following</span> 
          </div>
          <div className="profile-actions">
            <Link to={`/accounts/${user.username}/edit`}>
              <button>Edit Profile</button>
            </Link>
            <button>View Archive</button>
            <LogoutButton dispatch={dispatch} />
          </div>
        </div>
      </div>
      <div className='create-post-button-container' onClick={()=> setShowCreatePost(!showCreatePost)}>
      <i class="fa-regular fa-square-plus create-post-icon"></i>
      Create Post
      </div>

      {showCreatePost && (
        <div
        className="page-overlay"
        // onClick={() => handleAttachmentBoxToggle()}
      >
      <div className="create-post">
        <h2>Create New Post</h2>
        <form onSubmit={handleCreatePost} style={{ marginBottom: '2em' }}>
          <div>
            <label htmlFor="description">Caption:</label>
            <input
              type="text"
              id="caption"
              value={caption}
              onChange={(e) => setCaption(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="image">Upload Image:</label>
            <input
              type="file"
              id="image"
              accept="image/*"
              onChange={handleFileChange}
              required
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Creating Post...' : 'Create Post'}
          </button>
          {message && <p>{message}</p>}
          {error && <p className="error">{error}</p>}
        </form>
      </div>
      </div>
      )}

      <div className="profile-posts">
        <UserPosts posts={posts} />
      </div>
    </div>
  );
};

export default UserProfilePage;
