import React, { useState, useEffect } from 'react';
import { notificationRead, userNotification } from '../../api/services/userServices';

function Notification() {
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [notificationMarkAsRead, setNotificationMarkAsRead] = useState(false);

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        setLoading(true);
        setError('');
        const fetchedNotifications = await userNotification();
        setNotifications(fetchedNotifications);
        console.log('Fetched Notifications: ', fetchedNotifications);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchNotifications();

  }, []);

  const handleNotificationRead = async () => {
     try {
      const notificationIds = notifications.map((notification) => notification._id)
      const data = await notificationRead(notificationIds);
      setNotificationMarkAsRead(data)
      console.log()
      setNotifications((prevNotifications) =>
        prevNotifications.map((notification) =>
          notificationIds.includes(notification._id)
            ? { ...notification, read: true }
            : notification
        )
      );
     } catch (error) {
       setError(error.message)
     } finally {
       setLoading(false)
     }
    }


  if (loading) return <p>loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Notification</h2>
      <button onClick={handleNotificationRead}>Read</button>
      <ul>
        {notifications.map((notification, index) => (
          <li key={index}>
            <span className='notification-text'>{notification.sender.username}</span>
            <span> started following</span>
            <span>{notification.content}</span>
            <span style={{color:'red'}}> {notification.comment}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Notification;
