import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { postUserComment } from '../../api/services/userServices';

const UserComment = ({ postId }) => {
  const { state } = useAuthContext();
  const { user } = state;
  const [commentText, setCommentText] = useState('');
  const [userComments, setUserComments] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // useEffect(() => {
  //   const fetchComments = async () => {
  //     setLoading(true);
  //     try {
  //       const comments = await postUserComment(postId);
  //       setUserComments(comments);
  //     } catch (error) {
  //       setError(error.message);
  //     } finally {
  //       setLoading(false);
  //     }
  //   };

  //   fetchComments();
  // }, [postId]);

  const handleUserComment = async () => {
    setLoading(true);
    setError('');
    try {
      console.log("Post id: ", postId);
      const newComment = await postUserComment(postId, user.userId, commentText);
      setUserComments([...userComments, newComment]);
      console.log('Comment: ', newComment);
      console.log("User Comments: ", userComments)
      setCommentText('');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="comment-component">
      <div className="comment-list">
        {userComments.map((comment, index) => (
          <div className="comment-item" key={index}>
            <span>{comment.username}: {comment.text}</span>
          </div>
        ))}
      </div>
      <div className="comment-input">
        <input 
          type="text" 
          placeholder="Add a comment..." 
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={handleUserComment} disabled={loading}>
          {loading ? 'Posting...' : 'Post'}
        </button>
      </div>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default UserComment;
