import React, { useState, useEffect, useCallback } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { updateUserProfile } from '../../api/auth';
import { updateUserDataAction } from '../../context/auth/actions';


const UserInfo = () => {
  const { state, dispatch } = useAuthContext();
  const { user } = state;
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    username: user.username || '',
    name: user.name || '',
    lastName: user.lastName || '',
    age: user.age || '',
    email: user.email || '',
    newPassword: '',
    confirmPassword: '',
  });

  const [showDatePicker, setShowDatePicker] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  }, []);

  const handleDateClick = () => {
    setShowDatePicker(true);
  };

  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setFormData((prevFormData) => ({
      ...prevFormData,
      age: date,
    }));
    setShowDatePicker(false);
  };

  useEffect(() => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      username: user.username || '',
      name: user.name || '',
      lastName: user.lastName || '',
      age: user.age || '',
      email: user.email || '',
    }));
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateUserProfile(user.userId, formData);
      dispatch(updateUserDataAction(formData));
      setMessage('Successfully updated profile information');
    } catch (error) {
      console.error('Failed to update user profile:', error);
    }
  };

  return (
    <div className="user-info-modal-wrapper">
      <form onSubmit={handleSubmit}>
        {Object.entries(formData).map(([key, value]) => (
          <div className="form-inputs-update-profile" key={key}>
            <label htmlFor={key}>
              {key.charAt(0).toUpperCase() + key.slice(1)}:
            </label>
            {key === 'age' ? (
              <div>
                <input
                  type="text"
                  id="age"
                  name="age"
                  value={selectedDate || value}
                  onChange={handleChange}
                  onClick={handleDateClick}
                  readOnly
                />
                {showDatePicker && (
                  <DatePicker onSelect={handleDateSelect} />
                )}
              </div>
            ) : (
              <input
                type={key.includes('Password') ? 'password' : 'text'}
                id={key}
                name={key}
                value={value}
                onChange={handleChange}
                disabled={key === 'username'}
                autoComplete={key === 'password' ? 'on' : 'off'}
              />
            )}
          </div>
        ))}
        <button type="submit">Save Changes</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

const DatePicker = ({ onSelect }) => {
  // Logic for rendering the date picker UI
  return (
    <div className="date-picker">
      {/* Your date picker UI here */}
      {/* For example, a table with selectable dates */}
    </div>
  );
};

export default UserInfo;
