import { useState } from 'react';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';
import UserComment from './UserComment';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import React from 'react';
import { sliderSettings } from '../Slider/Slider';

const UserProfilePage = ({
  username,
  profilePicture,
  userPosts,
  followersCount,
  followingCount,
  posts,
}) => {
  const [selectedPost, setSelectedPost] = useState(null);

  const handleClickPost = (postId) => {
    setSelectedPost((prevSelectedPost) =>
      prevSelectedPost === postId ? null : postId
    );
  };

  const handleOverlayClick = () => {
    setSelectedPost(null);
  };

  useEscapeKeyHandler(() => setSelectedPost(null));

  return (
    <div className="user-profile-info">
      <div className="profile-header">
        <div className="user-photo-header">
          <img src={profilePicture} alt="Profile" />
          <h1>{username}</h1>
          <div className="btn-container-user-profile">
            <button className="edit-profile-user-btn">Edit Profile</button>
            <button className="view-archive-profile-user">View archive</button>
          </div>
          <div className="follow-info">
            <span>{userPosts} posts</span>
            <span>{followersCount} followers</span>
            <span>{followingCount} following</span>
          </div>
        </div>
      </div>
      <h2>
        <i className="fa-solid fa-table-cells"></i>Posts
      </h2>
      <div className="user-posts">
        {posts &&
          posts.map((post) => (
            <div key={post.id} className="post">
              {selectedPost === post.id ? (
                <div>
                  <Slider {...sliderSettings}>
                    {Array.isArray(post.imageUrl) ? (
                      post.imageUrl.map((image, index) => (
                        <div key={index}>
                          <img src={image} alt={`Post ${index + 1}`} />
                        </div>
                      ))
                    ) : (
                      <div>
                        <img src={post.imageUrl} alt={`Post ${post.id}`} />
                      </div>
                    )}
                  </Slider>
                  <UserComment
                    key={post.id}
                    imageUrl={
                      Array.isArray(post.imageUrl)
                        ? post.imageUrl[0]
                        : post.imageUrl
                    }
                    profilePicture={profilePicture}
                    username={username}
                  />
                </div>
              ) : (
                <div onClick={() => handleClickPost(post.id)}>
                  <Slider {...sliderSettings}>
                    {Array.isArray(post.imageUrl) ? (
                      post.imageUrl.map((image, index) => (
                        <div key={index}>
                          <img src={image} alt={`Post ${index + 1}`} />
                        </div>
                      ))
                    ) : (
                      <div>
                        <img src={post.imageUrl} alt={`Post ${post.id}`} />
                      </div>
                    )}
                  </Slider>
                </div>
              )}
              {selectedPost && (
                <div className="post-overlay" onClick={handleOverlayClick}></div>
              )}
            </div>
          ))}
      </div>
    </div>
  );
};

export default UserProfilePage;
