import React from 'react';

const UserPageContainer = () => {
  // Sample user data
  const user = {
    username: 'luka92',
    profilePicture:
      'https://st3.depositphotos.com/1017228/18878/i/450/depositphotos_188781580-stock-photo-handsome-cheerful-young-man-standing.jpg',
    userPosts: 32,
    followersCount: 100,
    followingCount: 50,
  };

  // Sample array of posts
  const posts = [
    {
      id: 1,
      imageUrl:
      "https://upload.wikimedia.org/wikipedia/commons/a/a4/Anatomy_of_a_Sunset-2.jpg",
      caption: 'Beautiful sunset!',
    },
    {
      id: 2,
      imageUrl:
        'https://as2.ftcdn.net/v2/jpg/04/13/92/53/1000_F_413925387_tHUX1eqBvqdQmGRJK09OktrDa89NPrR8.jpg',
      caption: 'Exploring nature!',
    },
    {
      id: 3,
      imageUrl:
        'https://us.images.westend61.de/0001061131pw/group-of-friends-having-fun-on-the-beach-running-into-the-water-WPEF01049.jpg',
      caption: 'Having fun at the beach!',
    },
    {
      id: 3,
      imageUrl:
        'https://us.images.westend61.de/0001061131pw/group-of-friends-having-fun-on-the-beach-running-into-the-water-WPEF01049.jpg',
      caption: 'Having fun at the beach!',
    },
    {
      id: 3,
      imageUrl:
        'https://us.images.westend61.de/0001061131pw/group-of-friends-having-fun-on-the-beach-running-into-the-water-WPEF01049.jpg',
      caption: 'Having fun at the beach!',
    },
    {
      id: 3,
      imageUrl:
        'https://us.images.westend61.de/0001061131pw/group-of-friends-having-fun-on-the-beach-running-into-the-water-WPEF01049.jpg',
      caption: 'Having fun at the beach!',
    },
  ];

  return <UserProfilePage {...user} posts={posts} />;
};

const UserProfilePage = ({
  username,
  profilePicture,
  userPosts,
  followersCount,
  followingCount,
  posts,
}) => {
  return (
    <div className="user-profile-info">
      <div className="profile-header">
        <div className="user-photo-header">
          <img src={profilePicture} alt="Profile" />
          <h1>{username}</h1>
          <div>
            <button className="edit-profile-user-btn">Edit Profile</button>
            <button className="view-archive-profile-user">View archive</button>
          </div>
          <div className="follow-info">
            <span>{userPosts} posts</span>
            <span>{followersCount} followers</span>
            <span>{followingCount} following</span>
          </div>
        </div>
      </div>
      <h2><i class="fa-solid fa-table-cells"></i>Posts</h2>
      <div className="user-posts">
        {posts &&
          posts.map((post) => (
              <div key={post.id} className="post">
                <img src={post.imageUrl} alt="Post" />
                {/* {/* <p>{post.caption}</p> */}
            </div>
          ))}
      </div>
    </div>
  );
};

export default UserPageContainer;
