import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { verifyEmail } from './'; // Import your API function for verifying email

function VerifyEmail() {
  const [verificationStatus, setVerificationStatus] = useState('');
  const { token } = useParams(); // Assuming you're using React Router's useParams hook to get the token from the URL

  useEffect(() => {
    const handleVerify = async () => {
      try {
        const response = await verifyEmail(token); // Call your API function to verify the email
        setVerificationStatus(response.message); // Assuming your API returns a message indicating the verification status
      } catch (error) {
        console.error('Error verifying email:', error);
        setVerificationStatus('Failed to verify email');
      }
    };

    handleVerify();
  }, [token]);

  return (
    <div>
      {verificationStatus === '' ? (
        <div>Verifying...</div>
      ) : (
        <div>{verificationStatus}</div>
      )}
    </div>
  );
}

export default VerifyEmail;
