import React, { useState } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { createNewPost } from '../../api/services/userServices';

const UserProfilePage = () => {
  const { state } = useAuthContext();
  const { user } = state;
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [caption, setCaption] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleCreatePost = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const postData = {
        caption,
        imageUrl,
        userId: user._id // Assuming you have the user ID available in the state
      };
      await createNewPost(postData);
      // Optionally, you can update the UI to display the newly created post
      console.log("Post: ", postData)
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  return (
    <div className="user-profile-info">
      <h2>Create New Post</h2>
      <form onSubmit={handleCreatePost}>
        <div>
          <label htmlFor="caption">Caption:</label>
          <input
            type="text"
            id="caption"
            value={caption}
            onChange={(e) => setCaption(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="imageUrl">Image URL:</label>
          <input
            type="text"
            id="imageUrl"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Creating Post...' : 'Create Post'}
        </button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default UserProfilePage;
