import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getUserProfile, getUserPosts } from '../../api/services/userServices';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import CreatePost from './CreatePost';
import { Link } from 'react-router-dom';
import OtherUserPosts from './OtherUsersPosts';

const OtherUserProfilePage = () => {
  const { state } = useAuthContext();
  const { user } = state;
  const { username } = useParams();
  const [userData, setUserData] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreatePost, setShowCreatePost] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userProfile = await getUserProfile(username);
        console.log("UserProfile: ", userProfile);
        const userPosts = await getUserPosts(username);
        setUserData(userProfile);
        setPosts(userPosts);
        console.log("User posts: ", userPosts);
      } catch (error) {
        console.error('Error fetching user profile or posts:', error);
        setError('Error fetching user profile or posts');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="profile-page">
      <div className="profile-header">
        {userData && (
          <>
            <img src={userData.userAvatar} alt="Profile" className="profile-picture" />
            <div className="profile-info">
              <h1>{userData.username}</h1>
              <div className="profile-stats">
                <span>{posts.length} posts</span>
                <span>{userData.followers.length} followers</span>
                <span>{userData.following.length} following</span>
              </div>
              {user.username === username && (
                <div className="profile-actions">
                  <Link to={`/accounts/${user.username}/edit`}>
                    <button>Edit Profile</button>
                  </Link>
                  <button>View Archive</button>
                </div>
              )}
            </div>
          </>
        )}
      </div>
      {user.username === username && (
        <div
          className="create-post-button-container"
          onClick={() => setShowCreatePost(!showCreatePost)}
        >
          <i className="fa-regular fa-square-plus create-post-icon"></i>
          Create Post
        </div>
      )}
      {showCreatePost && (
        <div className="page-overlay">
          <CreatePost user={user} setPosts={setPosts} onClose={() => setShowCreatePost(false)} />
        </div>
      )}
      <div className="profile-posts">
        <OtherUserPosts posts={posts} setPosts={setPosts} />
      </div>
    </div>
  );
};

export default OtherUserProfilePage;
