import React, { useState, useEffect, useCallback } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { updateUserProfile, verifyEmail } from '../../api/auth';
import { updateUserDataAction } from '../../context/auth/actions';

const UserInfo = () => {
  const { state, dispatch } = useAuthContext();
  const { user } = state;
  console.log('State: ', state);
  const [message, setMessage] = useState('');
  const [emailVerified, setEmailVerified] = useState(false);
  const [formData, setFormData] = useState({
    username: user.username || '',
    name: user.name || '',
    lastName: user.lastName || '',
    age: user.age || '',
    email: user.email || '',
    newPassword: '',
    confirmPassword: '',
  });

  useEffect(() => {
    // Check if user's email is verified
    setEmailVerified(user && user.emailVerified);
  }, [user]);

  const handleVerifyEmail = async () => {
    try {
      await verifyEmail(user.email);
      alert('Email verified successfully');
      setEmailVerified(true);
    } catch (error) {
      console.error('Failed to verify email', error);
    }
  };

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateUserProfile(user.userId, formData);
      dispatch(updateUserDataAction(formData));
      console.log('Form Data: ', formData);
      setMessage('Successfully updated profile information');
    } catch (error) {
      console.error('Failed to update user profile:', error);
    }
  };

  useEffect(() => {
    setFormData({
      username: user.username || '',
      name: user.name || '',
      lastName: user.lastName || '',
      age: user.age || '',
      email: user.email || '',
      newPassword: '',
      confirmPassword: '',
    });
  }, [user]);

    // Add useEffect to update email verification status after redirecting back from verify-email route
  useEffect(() => {
    if (window.location.pathname === '/verify-email') {
      setEmailVerified(true); // Assuming that if the user is redirected to this route, it means email is verified
    }
  }, []);

  return (
    <div className="user-info-modal-wrapper">
      <form onSubmit={handleSubmit}>
        {Object.entries(formData).map(([key, value]) => (
          <div className="form-inputs-update-profile" key={key}>
            <label htmlFor={key}>
              {key.charAt(0).toUpperCase() + key.slice(1)}:
            </label>
            <input
              type={key.includes('Password') ? 'password' : 'text'}
              id={key}
              name={key}
              value={value}
              onChange={handleChange}
              disabled={key === 'username'}
              autoComplete={key === 'password' ? 'on' : 'off'}
            />
            {key === 'email' && (
              <div className="verification-icon">
                {emailVerified ? (
                  <i
                    className="fa-solid fa-circle-check"
                    style={{ color: 'green' }}
                    title='Verified'
                  ></i>
                ) : (
                  <i
                    className="fa-solid fa-circle-xmark"
                    title='Email is not verified'
                    style={{ color: '#00050a' }}
                  ></i>
                )}
              </div>
            )}
          </div>
        ))}
        <button type="submit">Save Changes</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UserInfo;
