// PostDetail.js
import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import UserComment from './UserComment';
// import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { userLikes } from '../../api/services/userServices';

const PostDetail = ({ post }) => {
  const { state } = useAuthContext();
  const { user } = state;
  const [likes, setLikes] = useState(post.likes || []);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  console.log("User id: ", user.userId)

  const handleUserLike = async (postId) => {
    try {
        setLoading(true);
        setError('');
        const updatedLikes = await userLikes(postId, user.userId); 
        setLikes(updatedLikes.likes);
        console.log("Updated Likes", updatedLikes);
    } catch (error) {
        console.error(error.message);
    } finally {
        setLoading(false);
    }
  };
  useEffect(() => {
  }, [third])


  return (

    <div className="post-detail">

      <div className="post-detail-image-box">
        <img src={post.image} alt="Post" className="post-detail-image" />
      </div>
      <div className="post-detail-info">
        <div className="post-detail-userinfo">
          <div className="post-detail-image-box">
            <img src={user.userAvatar} alt="" />
          </div>
          <p>{user.username}</p>
        </div>

        <p>{post.description}</p>
        {/* <p>Username: {post.username}</p> */}
        <span onClick={() => handleUserLike(post._id)}>
          <i class="fa-regular fa-heart"></i>
        </span>
        <p>Likes: {likes.length}</p>
        <div>
          <p>Liked by:</p>
          <ul>
            {likes.map((like, index) => (
              <li key={index} style={{color:"red"}}>{like.username}</li>
            ))}
          </ul>
        </div>
        {/* <p>Comments: {post.comments}</p> */}
        {/* Add comments section here */}
        {/* <UserComment/> */}
      </div>
      <div>

      </div>
      {loading && (
            <p>Loading...</p>
        )}
         {error && <p>{error}</p>}
    </div>
  );
};

export default PostDetail;
