import React, { useState } from 'react';
import useEscapeKeyHandler from '../../Hooks/EscapeHandler';
import { BarLoader } from 'react-spinners';
import { handleAsyncOperation } from '../../utils/handleAsyncOperation';
import { resetPasswordRequest } from '../../api/services/userServices';

const ForgetPasswordModal = ({ onClose }) => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);


   await handleAsyncOperation(async () => {
     await resetPasswordRequest(email)
     onClose();
   })
  }

  //   try {
  //     const response = await fetch(
  //       'https://localhost:5500/api/users/reset-password',
  //       {
  //         method: 'POST',
  //         headers: {
  //           'Content-Type': 'application/json',
  //         },
  //         body: JSON.stringify({ email }),
  //       },
  //     );
  //     const data = await response.json();
  //     if (response.ok) {
  //       setMessage(data.message);
  //       onClose(); // Close the modal after submitting
  //     } else {
  //       setMessage(data.message);
  //     }
  //   } catch (error) {
  //     setMessage("");
  //   } finally {
  //     setLoading(false);
  //   }
  // };

  useEscapeKeyHandler(onClose);

  return (
    <div className="password-reset-modal">
      <span className="close" onClick={onClose}>
        &times;
      </span>
      <form onSubmit={handleSubmit}>
        <div className="password-reset-request-input">
          <label htmlFor="email">Recovery Email</label>
          <i className="fa-solid fa-envelope user-email"></i>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            autoComplete='on'
          />
          <button type="submit" disabled={loading}>
            Send
          </button>
        </div>
      </form>
      {loading && (
        <div className="bar-loader" style={{}}>
          <BarLoader color="#fe3c72" />
        </div>
      )}
      {message && <p>{message}</p>}
    </div>
  );
};

export default ForgetPasswordModal;
