import React, { useState, useEffect } from 'react';
import { usersData } from '../../api/users';
import { RingLoader } from 'react-spinners';
import { userComments } from '../../api/services/userServices';
import { useAuthContext } from '../../context/auth/AuthContextProvider';

const UserComment = ({ postId, userId, text }) => {
  const { state } = useAuthContext();
  const { user } = state;
  const [userComments, setUserComments] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const handleUserComment = async () => {
    setUserComments(null)
    setLoading(true);
    setError('');
    try {
      const result = await userComments(userId, postId,text)
      setUserComments(result);
    } catch (error) {
      setError(error)
    } finally {
      setLoading(false)
    }
  }


  return (
    <div className="comment-component">
      <div className="comment-list">
        {userComments.map((comment, index) => (
          <div className="comment-item" key={index}>
            {/* Assuming the comment object has properties like userId, username, and commentText */}
            <span>
              {comment.username}: {comment.commentText}
            </span>
          </div>
        ))}
      </div>
      <div className="comment-input">
        {/* <img src={profilePicutre} alt="" /> */}
        <input type="text" placeholder="Add a comment..." />
        <button onClick={() => handleUserComment(postId, userId, text)}>
          Post
        </button>
      </div>
    </div>
  );
};

export default UserComment;
