import React, { useState, useEffect, useCallback } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { updateUserProfile, verifyEmail } from '../../api/auth';
import { updateUserDataAction } from '../../context/auth/actions';

const UserInfo = () => {
  const { state, dispatch } = useAuthContext();
  const { user } = state;
  console.log("State: ", state );
  const [message, setMessage] = useState('');
  const [emailVerified, setEmailVerified] = useState(false)
  const [formData, setFormData] = useState({
    username: user.username || '',
    name: user.name || '',
    lastName: user.lastName || '',
    age: user.age || '',
    email: user.email || '',
    newPassword: '',
    confirmPassword: '',
  });


  useEffect(() => {
    // Check if user's email is verified
    setEmailVerified(user && user.emailVerified);
  }, [user]);

  const handleVerifyEmail = async () => {
    try {
      await verifyEmail(user.email)
      alert('Email verified successfully')
      setEmailVerified(true);
    } catch (error) {
      console.error("Failed to verify email", error)
    }
  } 

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateUserProfile(user.userId, formData);
      dispatch(updateUserDataAction(formData));
      console.log("Form Data: ", formData);
      setMessage('Successfully updated profile information');
    } catch (error) {
      console.error('Failed to update user profile:', error);
    }
  };

  useEffect(() => {
    setFormData({
      username: user.username || '',
      name: user.name || '',
      lastName: user.lastName || '',
      age: user.age || '',
      email: user.email || '',
      newPassword: '',
      confirmPassword: '',
    });
  }, [user]);




  return (
    <div className="user-info-modal-wrapper">
      <form onSubmit={handleSubmit}>
        {Object.entries(formData).map(([key, value]) => (
          <div className="form-inputs-update-profile" key={key}>
            <label htmlFor={key}>
              {key.charAt(0).toUpperCase() + key.slice(1)}:
            </label>
            <input
              type={key.includes('Password') ? 'password' : 'text'}
              id={key}
              name={key}
              value={value}
              onChange={handleChange}
              disabled={key === 'username'}
              autoComplete={key === 'password' ? 'on' : 'off'}
              />
              {key === 'email' && (
  <div className="verification-icon">
    {emailVerified ? (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="green" width="24px" height="24px">
        <path d="M0 0h24v24H0z" fill="none"/>
        <path d="M9 16.2l-3.5-3.5a.984.984 0 0 1 .7-1.7c.3 0 .5.1.7.3l2.8 2.8 6.1-6.1a.984.984 0 0 1 1.4 1.4l-6.8 6.8c-.4.4-1 .4-1.4 0z"/>
      </svg>
    ) : (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="red" width="24px" height="24px">
        <path d="M0 0h24v24H0z" fill="none"/>
        <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8zm1-14h-2v10h2V6z"/>
      </svg>
    )}
  </div>
)}
          </div>
        ))}
        <button type="submit">Save Changes</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UserInfo;
