import React, { useRef } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import styles from '../UserProfile/user.module.scss';

const UserProfile = () => {
  const { state } = useAuthContext();
  const { user } = state;
  const fileInputRef = useRef(false);

  const handleFileSelect = (event) => {
    var file = event.target.files[0];
    console.log('Selected file:', file);
    // Handle the selected file, for example, you can upload it to a server and update the user's profile picture
  };

  const handleClick = () => {
    fileInputRef.current.click(); // Trigger file input click when the user clicks on the profile picture
  };


    return (
      <div className={styles.userProfileContainer}>
        <p>{user.userName}</p>
        <div className={styles.profilePicture} onClick={handleClick}>
          {/* Display the current profile picture */}
          <img src={user.profilePicture} alt="Profile" />
          {/* Hidden file input element */}
          <input
            type="file"
            ref={fileInputRef} // Attach the ref to the file input element
            style={{ display: 'none' }} // Hide the file input
            onChange={handleFileSelect} // Attach onChange event handler
          />
        </div>
      </div>
    );
  };

export default UserProfile;
