import React, { useState } from 'react';
import { useAuthContext } from '../../context/auth/AuthContextProvider';

const UserInfo = () => {
  const { state } = useAuthContext();
  const { user } = state;

  const [formData, setFormData] = useState({
    username: user.username,
    name: user.name,
    lastName: user.lastName,
    age: user.age,
    email: user.email,
    newPassword: '',
    confirmPassword: '',
  });


  const {username, name, lastName, age, email, newPassword, confirmPassword} = formData;

  const handleChange = e => {
    setFormData({...formData, [e.target.value]: e.target.value});
  }

  console.log(form)

  const handleSubmit = e => {
    e.preventDefault();
    if(newPassword !== confirmPassword) {
      return;
    }

    // updateUser(formData);
  };

  return (
    <div className='user-info-modal-wrapper'>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" name="username" value={username} onChange={handleChange} />

        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={name} onChange={handleChange} />

        <label htmlFor="lastName">Last Name:</label>
        <input type="text" id="lastName" name="lastName" value={lastName} onChange={handleChange} />

        <label htmlFor="age">Age:</label>
        <input type="text" id="age" name="age" value={age} onChange={handleChange} />

        <label htmlFor="email">Email:</label>
        <input type="email" id="email" name="email" value={email} onChange={handleChange} />

        <label htmlFor="newPassword">New Password:</label>
        <input type="password" id="newPassword" name="newPassword" value={newPassword} onChange={handleChange} />

        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input type="password" id="confirmPassword" name="confirmPassword" value={confirmPassword} onChange={handleChange} />

        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default UserInfo;
