import { jwtDecode, jwtEncode } from 'jwt-decode'; // Import jwtEncode for encoding tokens

// Function to parse JWT token
function parseJWT(token) {
    const data = jwtDecode(token);
    return data;
}

// Function to check if token is valid
function isTokenValid(token) {
    const currentTime = Date.now() / 1000;
    const decoded = jwtDecode(token);
    return decoded.exp > currentTime;
}

// Function to toggle local storage based on token presence
function toggleLocalStorage(token) {
    if (token) {
        // Update token payload before storing it
        const updatedToken = updateTokenPayload(token);
        localStorage.setItem("accessToken", updatedToken);
    } else {
        localStorage.removeItem("accessToken");
    }
}

// Function to update token payload with new user data
function updateTokenPayload(token, newPayload) {
    try {
        // Decode the access token to access its payload
        const decodedToken = jwtDecode(token);

        // Update the payload with the new user data
        const updatedPayload = { ...decodedToken, ...newPayload };

        // Encode the updated payload to generate a new access token
        const updatedToken = jwtEncode(updatedPayload);

        return updatedToken;
    } catch (error) {
        console.error('Error decoding or updating token payload:', error);
        return token; // Return original token in case of error
    }
}

export { toggleLocalStorage, isTokenValid, parseJWT };
