import React, { useState, useEffect } from 'react';
import { fetchUserFollowers } from '../../api/services/userServices';

const UserFollowers = ({ userId, setFollowersCount }) => {
  const [followers, setFollowers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const handleFollowUser = async () => {
      try {
        setLoading(true);
        const followers = await fetchUserFollowers(userId);
        setFollowers(followers);
        setFollowersCount(followers.length); // Update followers count in parent component
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setMessage(error.message);
      }
    };

    handleFollowUser();
  }, [userId, setFollowersCount]);

  return (
    <div>
      {loading && <p>Loading...</p>}
      {message && <p>{message}</p>}
      <ul>
        {followers.map((follower) => (
          <li key={follower.id}>{follower.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default UserFollowers;
