import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getUserProfile, getUserPosts } from '../../api/services/userServices';

const OtherUserProfilePage = () => {
  const { username } = useParams();
  const [userData, setUserData] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userProfile = await getUserProfile(username);
        const userPosts = await getUserPosts(username);
        setUserData(userProfile);
        setPosts(userPosts);
      } catch (error) {
        setError('Error fetching user profile or posts');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <img src={userData.avatar} alt="Profile" className="profile-picture" />
        <div className="profile-info">
          <h1>{userData.username}</h1>
          <div className="profile-stats">
            <span>{userData.posts.length} posts</span>
            <span>{userData.followers.length} followers</span>
            <span>{userData.following.length} following</span>
          </div>
        </div>
      </div>

      <div className="profile-posts">
        {posts.map(post => (
          <div key={post._id} className="post-item">
            <img src={post.image} alt="Post" className="w-full h-auto rounded-lg" />
            <p>{post.caption}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OtherUserProfilePage;
