import React from 'react';
import { GoogleLogin } from 'react-google-login';
import { useAuthContext } from '../../context/auth/AuthContextProvider';
import { logInAction } from '../../context/auth/actions';
import { HOME_PAGE } from '../../constants/routes';
import { useNavigate } from 'react-router-dom';

const GoogleSignInButton = () => {
  const { dispatch } = useAuthContext();
  const navigate = useNavigate();

  const responseGoogle = (response) => {
    if (response.profileObj && response.tokenId) {
      console.log(response);
      // Send token to the backend for verification
      fetch('http://localhost:3000/api/users/auth/google', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ token: response.tokenId })
      })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          dispatch(logInAction(data.user));
          navigate(HOME_PAGE);
        } else {
          console.error('Authentication failed', data.message);
        }
      })
      .catch(err => {
        console.error('Backend authentication error', err);
      });
    } else {
      console.error('Google login failed: No profile or token found');
    }
  };

  const onFailure = (error) => {
    if (error.error === 'popup_closed_by_user') {
      console.log('User closed the Google Sign-In popup before completing the sign-in process.');
    } else {
      console.error('Google login failed', error);
    }
  };

  return (
    <GoogleLogin
      clientId="230625856455-h70i0k40gr1pf3d0jhfsms7ul8gjkk15.apps.googleusercontent.com"
      buttonText="Sign in with Google"
      onSuccess={responseGoogle}
      onFailure={onFailure}
      cookiePolicy={'single_host_origin'}
    />
  );
};

export default GoogleSignInButton;
