import React, {useState} from 'react'
import { userUnFollow } from '../../api/services/userServices'

function UnfollowButton(userId) {
    const [error, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const [unfollow, setUnfollow] = useState(false)

    const handleUnFollowUser = async () => {
        try {
            setLoading(true);
            const unfollowUser =  await userUnFollow(userId);
            setUnfollow(true)
            setError('')
        } catch (error) {
            console.error(error)
            setError(error.message)
        } finally {
            setLoading(false)
        }
    };


    if(loading) return <p>following...</p>
    if(error) return <p>{error}</p>


  return (
    <div>
        <button onClick={handleUnFollowUser} disabled={unfollow}>{unfollow ? 'following' : 'unfollow'}</button>
    </div>
  )
}

export default UnfollowButton;
